(142, 4, 72, 0, 28, '<p>Bộ S&aacute;ch B&agrave;i Tập Lớp 12 gồm:</p>\r\n<p>01. B&agrave;i tập giải t&iacute;ch 12</p>\r\n<p>02. B&agrave;i tập h&igrave;nh học 12</p>\r\n<p>03. B&agrave;i tập vật l&yacute; 12</p>\r\n<p>04. B&agrave;i tập h&oacute;a học 12</p>\r\n<p>05. B&agrave;i tập ngữ văn 12 - T.1</p>\r\n<p>06. B&agrave;i tập ngữ văn 12 - T.2</p>\r\n<p>07. B&agrave;i tập tin học 12</p>\r\n<p>08. B&agrave;i tập tiếng anh 12</p>\r\n<p>09. B&agrave;i tập sinh học 12</p>\r\n<p>10. B&agrave;i tập lịch sử 12</p>\r\n<p>11. B&agrave;i tập địa l&yacute; 12.</p>'),
(143, 4, 73, 0, 28, 'Bộ sách giúp các em học sinh ôn luyện và rèn luyện lại các kiến thức cơ bản và kĩ năng đã được học.'),
(144, 4, 83, 0, 28, NULL),
(145, 4, 106, 0, 28, NULL),
(150, 4, 72, 0, 29, '<p>Nội dung c&aacute;c b&agrave;i kiểm tra định k&igrave; bao gồm hệ thống c&acirc;u hỏi v&agrave; b&agrave;i tập tương ứng với c&aacute;c kĩ năng v&agrave; kiến thức của tất cả c&aacute;c ph&acirc;n m&ocirc;n: Tập đọc, Ch&iacute;nh tả, Luyện từ v&agrave; c&acirc;u, Tập l&agrave;m văn, gi&uacute;p c&aacute;c em học sinh r&egrave;n luyện v&agrave; l&agrave;m quen với c&aacute;c h&igrave;nh thức kiểm tra, đ&aacute;nh gi&aacute; trong c&aacute;c đợt kiểm tra định k&igrave; của năm học lớp 1.</p>\r\n<p>Cuốn s&aacute;ch n&agrave;y được bi&ecirc;n soạn tr&ecirc;n tinh thần b&aacute;m s&aacute;t chương tr&igrave;nh s&aacute;ch gi&aacute;o khoa Tiếng Việt 1, b&aacute;m s&aacute;t văn bản hướng dẫn chuẩn kiến thức, kĩ năng c&aacute;c m&ocirc;n học ở tiểu học, đồng thời cập nhật hướng dẫn điều chỉnh nội dung giảm tải theo văn bản mới nhất của Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo.</p>'),
(151, 4, 73, 0, 29, '<p>uyển Chọn Đề &Ocirc;n Luyện V&agrave; Tự Kiểm Tra Tiếng Việt Lớp 1 (Tập 1) cung cấp hệ thống đề kiểm tra định k&igrave; từng tuần của s&aacute;ch gi&aacute;o khoa Tiếng Việt 1 (Chương tr&igrave;nh Tiểu học mới).</p>'),
(152, 4, 83, 0, 29, NULL),
(153, 4, 106, 0, 29, NULL),
(154, 4, 72, 0, 30, 'Nội dung các bài kiểm tra định kì bao gồm hệ thống câu hỏi và bài tập tương ứng với các kĩ năng và kiến thức của tất cả các phân môn: Tập đọc, Chính tả, Luyện từ và câu, Tập làm văn, giúp các em học sinh rèn luyện và làm quen với các hình thức kiểm tra, đánh giá trong các đợt kiểm tra định kì của năm học lớp 2.\r\n\r\nCuốn sách này được biên soạn trên tinh thần bám sát chương trình sách giáo khoa Tiếng Việt 2, bám sát văn bản hướng dẫn chuẩn kiến thức, kĩ năng các môn học ở tiểu học, đồng thời cập nhật hướng dẫn điều chỉnh nội dung giảm tải theo văn bản mới nhất của Bộ Giáo dục và Đào tạo.'),
(155, 4, 73, 0, 30, 'Tuyển Chọn Đề Ôn Luyện Và Tự Kiểm Tra Tiếng Việt Lớp 2 (Tập 2) cung cấp hệ thống đề kiểm tra định kì từng tuần của sách giáo khoa Tiếng Việt 2 (Chương trình Tiểu học mới).'),
(156, 4, 83, 0, 30, NULL),
(157, 4, 106, 0, 30, NULL),
